mapper.select:

         SELECT *
	     FROM 	TB_CODE 
	     WHERE    1=1
	      and comm_cd     =   #{comm_cd}     
	      and CD_TYPE     =   #{cd_type}     
	      and comm_cd     !=  #{not_comm_cd}     
	      and length(comm_cd) =  #{code_len}     
	       and comm_cd     like '%' || #{right_like}     
	       and comm_cd     like #{left_like} || '%'     

          ORDER BY  ${ord} 
          ORDER BY ORD, COMM_CD


         SELECT count(*)
 	     FROM 	TB_CODE 
	     WHERE    1=1
	      and comm_cd     =   #{comm_cd}     
	      and CD_TYPE     =   #{cd_type}     
	      and comm_cd     !=  #{not_comm_cd}     
	      and length(comm_cd) =  #{code_len}     
	       and comm_cd     like '%' || #{right_like}     
	       and comm_cd     like #{left_like} || '%'

		   SELECT	*
		   FROM 	TB_CODE
		   WHERE  CD_TYPE = #{cd_type} 
           AND    COMM_CD = #{comm_cd}

mapper.update:
UPDATE TB_CODE
SET  CD_NM       = #{cd_nm   , jdbcType=VARCHAR} , 
     CD_DESC     = #{cd_desc , jdbcType=VARCHAR} ,
     ORD         = nvl(#{ord , jdbcType=INTEGER}, 0)  
WHERE CD_TYPE    = #{cd_type , jdbcType=VARCHAR}
AND   COMM_CD    = #{comm_cd , jdbcType=VARCHAR}

mapper.delete:
	   	 DELETE FROM  TB_CODE 
         WHERE CD_TYPE    = #{cd_type , jdbcType=VARCHAR}

          AND   COMM_CD    = #{comm_cd , jdbcType=VARCHAR}
