mapper.select:
		   SELECT	cd_type, comm_cd, cd_nm, cd_nm2, cd_desc, reg_dt
		   FROM 	TB_CODE
		   WHERE  CD_TYPE = #{cd_type} 
           AND    COMM_CD = #{comm_cd}

	       SELECT  *    
	       FROM TB_FILE
	       WHERE 1=1 
	            AND FILE_NO     = #{file_no}  
	            AND FILE_GBN    = #{file_gbn}	
	            AND REL_TBL     = #{rel_tbl}	
	            AND REL_KEY     = #{rel_key}  
	            AND FILE_GBN LIKE #{s_file_gbn} || '%' 


                    AND file_no IN 

                         #{idItem} 


  	       ORDER BY FILE_NO ASC

mapper.delete:
DELETE FROM  TB_FILE 
WHERE  FILE_NO = #{file_no , jdbcType=INTEGER}

 OR (REL_TBL  = #{rel_tbl} AND REL_KEY   = #{rel_key} )
